      {selectedDropPoint && (
        <Popup
          longitude={selectedDropPoint.longitude}
          latitude={selectedDropPoint.latitude}
          onClose={() => setSelectedDropPoint(null)}
          closeButton={false}
          anchor="bottom"
        >
          <div>
            <p>{selectedDropPoint.name}</p>
            <p>Distance: {selectedDropPoint.distance} km</p>
          </div>
        </Popup>
      )}

      {/* Geolocate control for user's current location */}
      <GeolocateControl
        positionOptions={{ enableHighAccuracy: true }}
        trackUserLocation={true}
        onGeolocate={(position) => {
          const { longitude, latitude } = position.coords;
          setUserLocation({ longitude, latitude });
          setViewState((prev) => ({ ...prev, longitude, latitude, zoom: 15 }));
        }}
      />
